export interface QuestStory {
    id: number
    quests: QuestItem[]
}

export interface QuestItem {
    id: number
    name: string
    desc?: string
    nodes: QuestNode[]
}

type ImprType = "All" | "Any" | "Empathy" | "Chaos" | "Morality" | "Wisdom" | "Benefit" //"共情" | "混沌" | "才智" | "道德" | "功利"

export function getImprType(type: ImprType): string {
    const nameMap = {
        All: "全部",
        Any: "任意",
        Empathy: "共情",
        Chaos: "混沌",
        Morality: "才智",
        Wisdom: "道德",
        Benefit: "功利",
    }
    return nameMap[type]
}

export function getRegionType(id: number): string {
    if (1011 === id) {
        return "海伯利亚"
    } else if (1041 === id) {
        return "华胥"
    }
    return "未知"
}

export interface Dialogue {
    id: number
    content: string
    npc?: number
    next?: number
    options?: DialogueOption[]
}

export interface QuestNode {
    id: string
    type: string
    name: string
    next?: string[]
    dialogues?: Dialogue[]
    answers?: DetectiveAnswer[]
    questions?: DetectiveQuestion[]
}

export interface DialogueOption {
    id: number
    content: string
    next?: number
    impr?: [number, ImprType, number]
    imprCheck?: [number, ImprType, number]
}

export interface DetectiveQuestion {
    id: number
    name: string
    answers: number[]
    tips: string
}

export interface DetectiveAnswer {
    id: number
    qid: number
    name: string
    desc: string
    icon: string
}

